// Vanilla JS 로 구현
const navBar = document.querySelector('.gnb');
var isShow = true;

document.querySelector('.menu_toggle_btn').onclick = function() {
  isShow ? navBar.style.display = 'block' : navBar.style.display = 'none';
  isShow = !isShow;
}